function xymat = visAng2xyNew(visualAngle,offsetAngle,monitor)distance2screen = monitor.viewDist;%	cmvAngle = visualAngle * pi/180;oAngle = offsetAngle * pi/180;screenSize = 2*monitor.center;pixpercm = [screenSize(1)/monitor.size(1) screenSize(2)/monitor.size(2)];xysize = distance2screen*(tan(vAngle+oAngle) - tan(oAngle));xymat = pixpercm(1) * xysize;xymat = round(xymat);